ts_plotter <- function(df){
  for(i in df$Code %>% unique()){
    print(data_1 %>% 
            filter(Code == i))
    for(j in colnames(df %>% select(-c(Year, Code)))){
      (autoplot(df %>% 
                  select(j) %>% 
                  ts(start = 2000)) +
         ggtitle(str_interp('${i}: ${j}')) +
         xlab('Year')) %>% print()
    }
  } 
}

