ts_plotter_2 <- function(df){
  for(i in 1:length(df$Code %>% unique())){
    print(df %>% 
            filter(Code == (df$Code %>% unique())[i]))
    for(j in 1:length(colnames(df %>% select(-c(Year, Code))))){
      (forecast::autoplot(df %>% 
                  filter(Code == (df$Code %>% unique())[i]) %>% 
         select(
           (colnames(df %>% 
                       select(-c(Year, Code))
           ))[j]) %>% 
         ts(start = 2000), facets=T)  +
        ggtitle(str_interp('${i}: ${j}')) +
        xlab('Year')) %>% print()
    }
  } 
}

